import React, { useRef, useEffect } from 'react';
import { Container } from './styles';
import axios from 'axios';

const Searchbar = () => {
	const stateRef = useRef();
	const cityRef = useRef();
	const [ cities, setCities ] = useState([ ]);
	const [ state, setState ] = useState([ ]);
	const url = 'https://indian-cities-api-nocbegfhqg.now.sh/cities?State=${}';
	useEffect(() => {
		axios.get(url).then((res) => {
			console.log(res)	
		});
	}, [ ]);
	return (
		<Container>
			<div>
					<label> Select State </label>
					<input list="states" placeholder="Select State" />
					<datalist id="states" ref={stateRef} >
						{states.map(item => (
							<option>{item}</option>
						))}
					</datalist>
			</div>
			<div>
				<label> Select City </label>
				<input list="cities" placeholder="Selecy City" className="cities" />
				<datalist  id="cities" ref={cityRef} >
					{cities.map(item => (
						<option></option>
					))}
				</datalist>
			</div>
			<div>
				<img src='/assets/addMarkerIcon.jpg' alt="Add Marker" width={80} height={80}  />
			</div>
			<div>
				<img src="/assets/profile.jpg" width={80} height={80} />
			</div>
		</Container>
	);
};
export default Searchbar;
