import React, { useRef, useEffect, useState } from 'react';
import { Container } from './styles';
import axios from 'axios';

const Searchbar = () => {
	const stateRef = useRef();
	const cityRef = useRef();
	const [ cities, setCities ] = useState([ ]);
	const [ state, setState ] = useState([ ]);
	const statesUrl = 'https://indian-cities-api-nocbegfhqg.now.sh/cities';

	const handleStateSelect = (event) => {
		const states = [];
		axios.get(statesUrl).then((res) => {
			res.data.map(item => {
				if(states.indexOf(item.State) < 0){
					states.push(item.State)
				}
			});
			setState(states);
		});
	};
	const handleCitySelect = (event) => {
		const cityUrl = `https://indian-cities-api-nocbegfhqg.now.sh/cities?State=${stateRef.current.value}`
		axios.get(cityUrl).then((res) => {
			res.data.map(item => console.log(item.State));
		});
	}
	return (
		<Container>
			<div>
					<label> Select State </label>
					<input list="states" placeholder="Select State" onInputCapture={(event) => handleStateSelect(event)}/>
					<datalist id="states" ref={stateRef}>
						{state && state.map(item => (
							<option key={i}>{item}</option>
						))}
					</datalist>
			</div>
			<div>
				<label> Select City </label>
				<input list="cities" placeholder="Selecy City" className="cities" onInput={() => handleCitySelect()} />
				<datalist  id="cities" ref={cityRef} >
					{cities && cities.map(item => (
						<option>{item}</option>
					))}
				</datalist>
			</div>
			<div>
				<img src='/assets/addMarkerIcon.jpg' alt="Add Marker" width={80} height={80}  />
			</div>
			<div>
				<img src="/assets/profile.jpg" width={80} height={80} />
			</div>
		</Container>
	);
};
export default Searchbar;
