import React, { useRef, useEffect, useState } from 'react';
import { Container } from './styles';
import axios from 'axios';

const Searchbar = () => {
	const stateRef = useRef();
	const cityRef = useRef();
	const [ cities, setCities ] = useState([ ]);
	const [ state, setState ] = useState([ ]);
	const statesUrl = 'https://indian-cities-api-nocbegfhqg.now.sh/cities';


	useEffect(() => {
		const states = [ ];
		axios.get(statesUrl).then((res) => {
			res.data.map(item => {
				if(states.indexOf(item.State) < 0){
					states.push(item.State)
				}
			});
			setState(states);
		});

	}, [ ]);

	const handleCitySelect = () => {
		const stateName = stateRef.current.value;
		const cityUrl = `https://indian-cities-api-nocbegfhqg.now.sh/cities?State=${stateName}`
		axios.get(cityUrl).then((res) => {
			res.data.map(item => {
				setCities((oldArray) => [ ...oldArray, item.City ]);
			});
		});	
	};
	return (
		<Container>
			<div>
					<label> Select State </label>
					<select id="state" ref={stateRef}>
						{state && state.map((item, i) => (
							<option key={i} value={item}>{item}</option>
						))}
					</select>
			</div>
			<div>
				<label> Select City </label>
				<select id="city" ref={cityRef} onChange={() => handleCitySelect()}>
					{cities && cities.map((item, i) => (
						<option key={i} value={item}>{item}</option>
					))}
				</select>
			</div>
			<div>
				<img src='/assets/bars.png' alt="Add Marker" width={50} height={40}  />
			</div>
			<div>
				<img src="/assets/profile_logo.png" width={50} height={50} />
			</div>
		</Container>
	);
};
export default Searchbar;
